package tic_tac_toe.player;

public class Player  {

	char piece;
	String name;
	
	public Player(char c, String s){
		piece=c;
		name=s;
	}
	


	public char getPiece() {
		// TODO Auto-generated method stub
		return piece;
		
	}



	public String getName() {
		// TODO Auto-generated method stub
		return name;

	}

}
